kfyorx rmgviuzxv KilwfxgUvrtmXorvmg {
    @TvgNzkkrmt("/kilwfxg/tvguoldvi")
    Ivhfog<Kilwfxgh> tvguoldvi(@IvjfvhgKzizn Rmgvtvi rw);  // 明确泛型类型
