package com.Luoyi.controller;

import com.Luoyi.bean.OrderDTO;
import com.Luoyi.bean.OrderItems;
import com.Luoyi.bean.Orders;
import com.Luoyi.bean.Result;
import com.Luoyi.server.OrderService;
import org.springframework.beans.BeanUtils;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import java.util.UUID;

@RequestMapping("/order")
@RestController
public class OrderController {
    @Autowired
    private OrderService orderService;
    /*
     * @Description: 生成订单 订单明细
     * @param orderDTO
     * @return bean.Result
     * @Author: 落一.
     * @Date: 2025/5/22 20:24
     */
    @RequestMapping("/create")
    public Result createOrder(OrderDTO orderDTO){
        Orders orders = new Orders();
        OrderItems orderItems = new OrderItems();
        //设置值
        BeanUtils.copyProperties(orderDTO,orders);
        BeanUtils.copyProperties(orderDTO,orderItems);
        //生成订单编号
        String uuid =UUID.randomUUID().toString().replace("-","");
        //设置订单编号，已支付，代发货
        orders.setOrderNo(uuid);
        orders.setPayStatus(1);
        orders.setOrderStatus(0);
        //返回订单id
        Integer orderid = orderService.createOrder(orders);
        //创建失败
        if (orderid == null && orderid == 0){
            return Result.fail("订单生成失败，请重试");
        }
        //创建订单明细
        orderItems.setOrderId(orderid);

        orderService.createOrderItem(orderItems);

        return Result.success();
    }


}

