@Getter
@AllArgsConstructor
public enum ResultCode {
    /** 200: 请求成功 */
    SUCCESS(200, "操作成功"),

    /** 400: 客户端请求参数错误 */
    BAD_REQUEST(400, "参数错误"),

    /** 401: 未授权/认证失败 */
    UNAUTHORIZED(401, "未授权"),

    /** 403: 无权限访问 */
    FORBIDDEN(403, "禁止访问"),

    /** 404: 请求的资源不存在 */
    NOT_FOUND(404, "资源不存在"),

