// 1. 修改Feign接口定义
@FeignClient(name = "product-service")
public interface ProductFeignClient {
    @GetMapping("/product/getflower")
    Result<Products> getflower(@RequestParam Integer id);  // 明确泛型类型
}

// 2. 直接使用泛型结果
Result<Products> flowerResult = productFeignClient.getflower(productId);
Products flower = flowerResult.getData();  // 无需强制转换

public class Result<T> {
    private Integer code;
    private T data;  // 关键：使用泛型T
    private String message;

    // getter/setter
}

for (OrderItems orderItem : orderItems) {
    Integer productId = orderItem.getProductId();
    Result<Products> flowerResult = productFeignClient.getflower(productId);

    // 安全检查
    if (flowerResult.getCode() == 200 && flowerResult.getData() != null) {
        Products flower = flowerResult.getData();  // 自动泛型转换
        orderVO.setOrderName(flower.getName());
        orderVO.setImage(flower.getImageUrl());
    } else {
        // 错误处理
        orderVO.setOrderName("商品信息缺失");
        orderVO.setImage("default.png");
    }
    orderVO.setQuantity(orderItem.getQuantity());
}

