/**
 * ResultCode enumeration defines standard HTTP status codes and messages
 * for API responses. It uses Lombok annotations to generate getters and
 * constructor automatically.
 */
package com.Luoyi.bean;

import lombok.AllArgsConstructor;
import lombok.Getter;

/**
 * Enumeration of standard result codes for API responses.
 * Each enum constant represents an HTTP status code with corresponding message.
 */
@Getter
@AllArgsConstructor
public enum ResultCode {
    /** 200: Successful operation */
    SUCCESS(200, "操作成功"),

    /** 400: Client sent invalid request */
    BAD_REQUEST(400, "参数错误"),

    /** 401: Authentication failed or not provided */
    UNAUTHORIZED(401, "未授权"),

    /** 403: Authenticated but not authorized */
    FORBIDDEN(403, "禁止访问"),

    /** 404: Requested resource not found */
    NOT_FOUND(404, "资源不存在"),

    /** 500: Unexpected server error */
    INTERNAL_SERVER_ERROR(500, "服务器错误"),

    /** 503: Service temporarily unavailable */
    SERVICE_UNAVAILABLE(503, "服务不可用");

    /** The numeric HTTP status code */
    private final Integer code;

    /** Human-readable message describing the status */
    private final String message;
}
```

