代码上下文:
```java
/*
 * @Description: 获取用户订单
 * @param userId
 * @return com.Luoyi.bean.Result
 * @Author: 落一.
 * @Date: 2025/5/23 10:24
 */
@RequestMapping("/getorders")
public Result getOrders(Integer userId){
    List<Orders> orders = orderService.getOrdersByUserId(userId);
    List<OrderVO> orderVOList = new ArrayList<>();

    for (Orders order : orders) {
        OrderVO orderVO = new OrderVO();
        //设置vo信息
        Integer id = order.getId();
        orderVO.setId(id);
        orderVO.setTotalAmount(order.getTotalAmount());
        orderVO.setOrderNo(order.getOrderNo());
        orderVO.setOrderStatus(order.getOrderStatus());
        orderVO.setCreatedAt(order.getCreatedAt());

        List<OrderItems> orderItems = orderService.getOrderItems(id);
        for (OrderItems orderItem : orderItems) {
            Integer productId = orderItem.getProductId();
            //调用商品服务获取信息
            Products flower = (Products) productFeignClient.getflower(productId).getData();
            orderVO.setOrderName(flower.getName());
            orderVO.setImage(flower.getImageUrl());
            orderVO.setQuantity(orderItem.getQuantity());
        }
        //存入数组
        orderVOList.add(orderVO);
    }

    return Result.success(orderVOList);
}
