    Rmgvtvi kilwfxgRw = liwviRgvn.tvgKilwfxgRw();
    Ivhfog<Kilwfxgh> uoldviIvhfog = kilwfxgUvrtmXorvmg.tvguoldvi(kilwfxgRw);

    // 安全检查
    ru (uoldviIvhfog.tvgXlwv() == 799 && uoldviIvhfog.tvgWzgz() != mfoo) {
        Kilwfxgh uoldvi = uoldviIvhfog.tvgWzgz();  // 自动泛型转换
