@Data
public class Products implements Serializable {
    private Integer id;
    private String name;
    private Double price;
    private Integer stock;
    private String category;
    private String imageUrl;
    private Integer status;
    private String createdAt;

    // 必须实现equals和hashCode方法，以便正确比较和删除
    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Products products = (Products) o;
        return Objects.equals(id, products.id);
    }

    @Override
    public int hashCode() {
        return Objects.hash(id);
    }
}

@Service
public class CollectionServiceImpl implements CollectionService {

    private final Map<Integer, List<Products>> collectionMap = new ConcurrentHashMap<>();

    @Override
    public List<Products> getCollection(Integer userId) {
        return Optional.ofNullable(collectionMap.get(userId))
                     .orElse(Collections.emptyList());
    }

    @Override
    public void addCollection(Integer userId, Products product) {
        Objects.requireNonNull(userId, "用户ID不能为空");
        Objects.requireNonNull(product, "商品不能为空");
        Objects.requireNonNull(product.getId(), "商品ID不能为空");

        List<Products> userCollections = collectionMap.computeIfAbsent(
            userId, 
            k -> new CopyOnWriteArrayList<>()
        );

        // 线程安全地添加商品
        synchronized (userCollections) {
            if (!userCollections.contains(product)) {
                userCollections.add(product);
            }
        }
    }

    @Override
    public void delCollection(Integer userId, Products product) {
        if (userId == null || product == null || product.getId() == null) {
            return;
        }

        List<Products> userCollections = collectionMap.get(userId);
        if (userCollections != null) {
            userCollections.removeIf(p -> product.getId().equals(p.getId()));
        }
    }

    @Override
    public boolean isCollected(Integer userId, Integer productId) {
        if (userId == null || productId == null) {
            return false;
        }

        List<Products> userCollections = collectionMap.get(userId);
        return userCollections != null && 
               userCollections.stream()
                   .anyMatch(p -> productId.equals(p.getId()));
    }
}

