package com.Luoyi.server.impl;

import com.Luoyi.bean.Products;
import com.Luoyi.server.CollectionService;
import org.springframework.stereotype.Service;

import java.util.ArrayList;
import java.util.List;
import java.util.Map;

@Service
public class CollectionServiceImpl implements CollectionService {
    private final Map<Integer, List<Products>> collectionlist = null;
    /*
     * @Description: 获取用户收藏
     * @param userid
     * @return java.util.Map<java.lang.Integer,java.util.List<com.Luoyi.bean.Products>>
     * @Author: 落一.
     * @Date: 2025/5/26 14:58
     */
    @Override
    public List<Products> getCollection(Integer userid) {
        List<Products> productsList = collectionlist.get(userid);
        if (productsList == null){
            return new ArrayList<>();
        }else {
            return productsList;
        }
    }

    /*
     * @Description: 添加收藏
     * @param products
     * @return void
     * @Author: 落一.
     * @Date: 2025/5/26 14:34
     */
    public void addCollection(Integer userid,Products products){
        //获取指定用户的收藏列表
        List<Products> productsList = collectionlist.get(userid);
        //为空就新建
        if (productsList.size() == 0 || productsList == null){
            List<Products> newlist = new ArrayList<>();
            newlist.add(products);
        }
    }
    /*
     * @Description: 移除收藏
     * @param key
     * @return void
     * @Author: 落一.
     * @Date: 2025/5/26 14:34
     */
    public void delCollection(Integer userid,Products products){
        List<Products> productsList = collectionlist.get(userid);
        productsList.remove(products);
    }
    /*
     * @Description: 清空收藏
     * @param
     * @return void
     * @Author: 落一.
     * @Date: 2025/5/26 14:35
     */
    public void clearAllCollection(Integer userid){
        List<Products> productsList = collectionlist.get(userid);
        productsList.clear();
    }
}

