import java.util.List;
import java.util.Map;

@Service
public class CollectionServiceImpl implements CollectionService {
    private final Map<Integer, List<Products>> collectionlist = null;
    /*
     * @Description: 获取用户收藏
     * @param userid
     * @return java.util.Map<java.lang.Integer,java.util.List<com.Luoyi.bean.Products>>
     * @Author: 落一.
     * @Date: 2025/5/26 14:58
     */
    @Override
    public List<Products> getCollection(Integer userid) {
        if (collectionlist == null){
            return new ArrayList<>();
        }
        if (!collectionlist.containsKey(userid)) {
            return new ArrayList<>();
        }
        List<Products> productsList = collectionlist.get(userid);
        if (productsList == null){
            return new ArrayList<>();
        }else {
            return productsList;
        }
    }

    /*
     * @Description: 添加收藏
     * @param products
     * @return void
     * @Author: 落一.
     * @Date: 2025/5/26 14:34
     */
    public void addCollection(Integer userid,Products products){
