        List<Products> productsList = collectionlist.get(userid);
        //为空就新建
        if (productsList.size() == 0 || productsList == null){
            List<Products> newlist = new ArrayList<>();
            newlist.add(products);
        }
    }
    /*
     * @Description: 移除收藏
     * @param key
     * @return void
     * @Author: 落一.
     * @Date: 2025/5/26 14:34
     */
    public void delCollection(Integer userid,Products products){
        List<Products> productsList = collectionlist.get(userid);
        productsList.remove(products);
    }
    /*
     * @Description: 清空收藏
     * @param
     * @return void
     * @Author: 落一.
     * @Date: 2025/5/26 14:35
     */
    public void clearAllCollection(Integer userid){
        List<Products> productsList = collectionlist.get(userid);
        productsList.clear();
    }
}

